Create a new Rust module for git worktree operations in src/git_worktree.rs. This module should provide core functionality for managing git worktrees.

Implement the following:

```rust
// src/git_worktree.rs
use std::process::Command;
use std::path::{Path, PathBuf};
use std::io::Result;
use chrono::Local;
use serde::{Deserialize, Serialize};

#[derive(Debug, Clone)]
pub struct Worktree {
    pub name: String,
    pub path: PathBuf,
    pub branch: String,
    pub created_at: String,
}

impl Worktree {
    pub fn new(phase_id: &str) -> Self {
        let timestamp = Local::now().format("%Y%m%d_%H%M%S").to_string();
        let name = format!("claude-phase-{}-{}", phase_id, timestamp);
        let path = PathBuf::from(format!("../{}", name));
        Self {
            name: name.clone(),
            path,
            branch: name,
            created_at: timestamp,
        }
    }
}

pub fn create_worktree(phase_id: &str, base_branch: &str) -> Result<Worktree> {
    let worktree = Worktree::new(phase_id);

    // Create worktree with new branch
    let output = Command::new("git")
        .args(&["worktree", "add", "-b", &worktree.branch, worktree.path.to_str().unwrap(), base_branch])
        .output()?;

    if !output.status.success() {
        return Err(std::io::Error::new(
            std::io::ErrorKind::Other,
            format!("Failed to create worktree: {}", String::from_utf8_lossy(&output.stderr))
        ));
    }

    Ok(worktree)
}

pub fn remove_worktree(worktree_name: &str) -> Result<()> {
    // Remove worktree
    let output = Command::new("git")
        .args(&["worktree", "remove", format!("../{}", worktree_name).as_str(), "--force"])
        .output()?;

    if !output.status.success() {
        return Err(std::io::Error::new(
            std::io::ErrorKind::Other,
            format!("Failed to remove worktree: {}", String::from_utf8_lossy(&output.stderr))
        ));
    }

    // Prune worktree refs
    Command::new("git")
        .args(&["worktree", "prune"])
        .output()?;

    Ok(())
}

pub fn list_claude_worktrees() -> Result<Vec<Worktree>> {
    let output = Command::new("git")
        .args(&["worktree", "list", "--porcelain"])
        .output()?;

    if !output.status.success() {
        return Err(std::io::Error::new(
            std::io::ErrorKind::Other,
            "Failed to list worktrees".to_string()
        ));
    }

    let output_str = String::from_utf8_lossy(&output.stdout);
    let mut worktrees = Vec::new();

    // Parse worktree list output
    let mut current_path = None;
    let mut current_branch = None;

    for line in output_str.lines() {
        if line.starts_with("worktree ") {
            current_path = Some(line.trim_start_matches("worktree ").to_string());
        } else if line.starts_with("branch refs/heads/") {
            current_branch = Some(line.trim_start_matches("branch refs/heads/").to_string());

            if let (Some(path), Some(branch)) = (&current_path, &current_branch) {
                if branch.starts_with("claude-phase-") {
                    let parts: Vec<&str> = branch.split('-').collect();
                    if parts.len() >= 4 {
                        let timestamp = parts[3..].join("-");
                        worktrees.push(Worktree {
                            name: branch.clone(),
                            path: PathBuf::from(path),
                            branch: branch.clone(),
                            created_at: timestamp,
                        });
                    }
                }
            }
        }
    }

    Ok(worktrees)
}

pub fn cleanup_old_worktrees(max_worktrees: usize) -> Result<()> {
    let mut worktrees = list_claude_worktrees()?;

    if worktrees.len() <= max_worktrees {
        return Ok(());
    }

    // Sort by creation time (oldest first)
    worktrees.sort_by(|a, b| a.created_at.cmp(&b.created_at));

    // Remove oldest worktrees
    let to_remove = worktrees.len() - max_worktrees;
    for worktree in worktrees.iter().take(to_remove) {
        println!("Removing old worktree: {}", worktree.name);
        remove_worktree(&worktree.name)?;
    }

    Ok(())
}
```

